mapper.update:
update tb_planner set plan_start = TO_DATE(Translate(SUBSTR ( #{plan_start} , 1, 19),'T',' '),'YYYY-MM-DD
HH24:MI:SS'), plan_end = TO_DATE(Translate(SUBSTR ( #{plan_end} , 1, 19),'T',' '),'YYYY-MM-DD
HH24:MI:SS') where plan_seq = #{plan_seq}

mapper.select:
		SELECT time_subject,TO_CHAR(TRUNC(time_count/3600), 'FM900')
                  ||':'||TO_CHAR(TRUNC(MOD(time_count, 3600) / 60), 'FM00')
                  ||':'||TO_CHAR(TRUNC(MOD(time_count, 60  )     ), 'FM00') as count ,TO_CHAR(time_date) as timedate from tb_timer where mem_seq = ${mem_seq}
